function jouerNotes () {
    listeNotes = Partition2.split(";")
    nmbrNotes = listeNotes.length / 2
    for (let index = 0; index <= nmbrNotes * 2 - 1; index++) {
        note = parseFloat(listeNotes[index])
        index += 1
        durée = parseFloat(listeNotes[index])
        if (note == 0) {
            music.rest(durée)
        } else {
            music.play(music.tonePlayable(note / 1, durée / 1), music.PlaybackMode.UntilDone)
        }
    }
    Partition2 = "1000"
    Noire = convertToText("")
    Silences$ = convertToText("")
    music.setTempo(120)
}
