function jouerPartition () {
    listeNotes = Partition2.split(";")
    nmbrNotes = listeNotes.length / 1
    for (let index = 0; index <= nmbrNotes * 2 - 1; index++) {
        note = parseFloat(listeNotes[index]) / 1
        index += 1
        durée = parseFloat(listeNotes[index]) * 1
        if (note == 0) {
            music.rest(durée)
        } else {
            music.play(music.createSoundExpression(
            WaveShape.Square,
            note,
            note,
            255,
            255,
            durée,
            SoundExpressionEffect.None,
            InterpolationCurve.Linear
            ), music.PlaybackMode.UntilDone)
        }
    }
    Partition2 = ""
}
