function jouerMélodie () {
    listeNotes = Partition2.split(",")
    nmbrNotes = listeNotes.length
    if (Noire == "") {
        Noire = convertToText(60000 / music.tempo())
        for (let index = 0; index <= nmbrNotes; index++) {
            Noire = "" + Noire + "," + convertToText(15000 / music.tempo())
        }
    }
    listeDurées = Noire.split(",")
    if (Silences$ == "") {
        Silences$ = convertToText(0)
        for (let index = 0; index <= nmbrNotes; index++) {
            Silences$ = "" + Silences$ + "," + convertToText("0")
        }
    }
    listeSilences = Silences$.split(",")
    for (let index = 0; index <= nmbrNotes; index++) {
        note = parseFloat(listeNotes[index])
        durée = parseFloat(listeDurées[index])
        silence = parseFloat(listeSilences[index])
        music.play(music.tonePlayable(note, durée), music.PlaybackMode.UntilDone)
        basic.pause(silence)
    }
    Partition2 = "1000"
    Noire = convertToText("")
    Silences$ = convertToText("")
    music.setTempo(120)
}
